startEkhon();function startEkhon(){const vw=Math.max(document.documentElement.clientWidth||0,window.innerWidth||0);const vh=Math.max(document.documentElement.clientHeight||0,window.innerHeight||0);const mainDiv=document.getElementById(`md`);const mainNavbar=document.getElementById(`mn`);const btnOkay=document.getElementById(`ekhon-accha`);const introVideoDiv=document.getElementById(`ekhon-intro`);const introVideo=document.getElementById(`ekhon-intro-vid`);const mainDivOfThisStory=document.getElementById(`ekhon-md`);const entranceLines=Array.from(document.getElementsByClassName(`ekhon-entrance`));const transitionTimeInMS=2e3;const transitionSpeedCurve=`linear`;const entranceLineAppearanceIntervalInMS=1e3;const mainNavbarHeight=mainNavbar.offsetHeight;introVideo.style.width=`100%`;introVideo.style.height=`${vh-mainNavbarHeight-6}px`;introVideo.style.objectFit=`cover`;entranceLines.forEach(line=>{line.style.opacity=`0`;line.style.transition=`opacity ${transitionTimeInMS}ms ${transitionSpeedCurve}`});window.onload=function(){document.getElementById(`ekhon-mt`).click()};mainNavbar.classList.remove(`mb-3`);mainDiv.classList.remove(`mb-4`);btnOkay.addEventListener(`click`,function(){introVideoDiv.classList.remove(`d-none`);introVideo.play();introVideo.addEventListener(`ended`,postIntroVideoRoutine)});function postIntroVideoRoutine(){introVideoDiv.classList.add(`d-none`);mainNavbar.classList.add(`mb-3`);mainDiv.classList.add(`mb-4`);mainDivOfThisStory.classList.remove(`d-none`);fadeInEntranceLines()}function fadeInEntranceLines(){entranceLines.forEach((line,index)=>{let lineWillAppearAfterMS=entranceLineAppearanceIntervalInMS*(index+1);setTimeout(()=>{line.style.opacity=`1`},lineWillAppearAfterMS)})}}