startEkhon();function startEkhon(){document.head.innerHTML+=`<link rel="stylesheet" href="css/ekhon.min.css">`;const vh=Math.max(document.documentElement.clientHeight||0,window.innerHeight||0);const mainDivOfBase=document.getElementById(`md`);const mainNavbar=document.getElementById(`mn`);const btnModalOkay=document.getElementById(`ekhon-modal-okay`);const introVideoDiv=document.getElementById(`ekhon-intro`);const introVideo=document.getElementById(`ekhon-intro-vid`);const mainDivs=[document.getElementById(`ekhon-md-1`),document.getElementById(`ekhon-md-2`),document.getElementById(`ekhon-md-3`)];const midVideoDiv=mainDivs[1];const midVideo=document.getElementById(`ekhon-mid-vid`);const entranceElements=Array.from(document.getElementsByClassName(`ekhon-entrance`));const playButton=document.querySelector(`.play-btn`);const playButtonIconHTML=`<i class="bi bi-play-circle"></i>`;const transitionTimeInMS=2e3;const transitionSpeedCurve=`linear`;const entranceElementAppearanceIntervalInMS=100;let arePaddingsInBaseState=true;const mainNavbarHeight=mainNavbar.offsetHeight;introVideo.style.width=`100%`;introVideo.style.height=`${vh-mainNavbarHeight-6}px`;introVideo.style.objectFit=`cover`;playButton.innerHTML+=playButtonIconHTML;entranceElements.forEach(element=>{element.style.opacity=`0`;element.style.transition=`opacity ${transitionTimeInMS}ms ${transitionSpeedCurve}`});window.onload=function(){postIntroVideoRoutine()};switchPaddings();btnModalOkay.addEventListener(`click`,function(){postIntroVideoRoutine()});function postIntroVideoRoutine(){introVideoDiv.classList.add(`d-none`);switchPaddings();mainDivs.forEach(mainDiv=>{mainDiv.classList.remove(`d-none`)});fadeInEntranceElements();let isMidVideoDivListening=true;midVideoDiv.addEventListener(`click`,playAndPauseVideo);midVideo.addEventListener(`ended`,function(){if(isMidVideoDivListening){midVideoDiv.removeEventListener(`click`,playAndPauseVideo);isMidVideoDivListening=false}})}function switchPaddings(){if(arePaddingsInBaseState){mainNavbar.classList.remove(`mb-3`);mainDivOfBase.classList.remove(`mb-4`)}else{mainNavbar.classList.add(`mb-3`);mainDivOfBase.classList.add(`mb-4`)}arePaddingsInBaseState=!arePaddingsInBaseState}function fadeInEntranceElements(){entranceElements.forEach((element,index)=>{let elementWillAppearAfterMS=entranceElementAppearanceIntervalInMS*(index+1);setTimeout(()=>{element.style.opacity=`1`},elementWillAppearAfterMS)})}function playAndPauseVideo(){if(midVideo.paused){midVideo.play();playButton.textContent=``}else{midVideo.pause();playButton.innerHTML+=playButtonIconHTML}}}